--select*from restaurant where r_name like '%기%';
create table restaurant (
          r_name varchar2(30) not null,
          type varchar2(30) not null,
          location varchar2(150) not null,
          best_yn char(1) default 'N',
          grade number default 3,
          description varchar2(3000),
          category varchar2(50),
          r_phone varchar2(30),
          parking_yn char(1) default 'N',
          open_time number,
          close_time number,
          holiday varchar2(30),
          
          constraint pk_restaurant_r_name primary key (r_name),
          constraint ck_restaurant_best check (best_yn in ('Y','N')),
          constraint ck_restaurant_grade check (grade between 0 and 5),
          constraint ck_restaurant_parking check (parking_yn in ('Y','N')),
          constraint ck_restaurant_open check (open_time between 0 and 2400),
          constraint ck_restaurant_close check (close_time between 0 and 2400)
);

--drop table restaurant;

