create table restaurant_menu (
          menu_no number,
          r_name varchar2(30),
          menu varchar2(100),
          price number,
          recommend char(1) default 'N',
          single_yn char(1) default 'Y',
          
          constraint pk_restaurant_menu_no primary key(menu_no),
          constraint fk_r_name_menu foreign key (r_name) references restaurant(r_name) on delete cascade,
          constraint ck_menu_recommend check (recommend in ('Y','N')),
          constraint ck_menu_single_yn check (single_yn in ('Y','N'))
);
